import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import pokemonLogo from "./images/pokemon.png";
import { Col, Container, Row, Spinner } from "reactstrap";
import backgroundImg from "./images/pokedex.webp";
function App() {
  const [pokemonData, setPokemonData] = useState({ results: [] });
  const [isLoading, setIsLoading] = useState(false);
  const [fetchMore, setFetchMOre] = useState(false);
  useEffect(() => {
    if (fetchMore) {
      setIsLoading(true);
      axios.get(pokemonData.next).then((res) => {
        // console.info(res, res);
        setPokemonData({
          ...pokemonData,
          ...res.data,
          results: [...pokemonData.results, ...res.data.results],
        });
      });
    }
    setIsLoading(false);
    setFetchMOre(false);
  }, [fetchMore]);
  const handleScroll = () => {
    if (
      document.documentElement.scrollHeight - 30 <
      document.documentElement.scrollTop + document.documentElement.clientHeight
    ) {
      setFetchMOre(true);
    }
  };
  useEffect(() => {
    document.addEventListener("scroll", handleScroll);
    axios.get("https://pokeapi.co/api/v2/pokemon/").then((res) => {
      setPokemonData(res.data);
    });
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div className="App" style={{ backgroundImage: `url(${backgroundImg})` }}>
      <nav
        className="navbar fixed-top nav-height"
        style={{ backgroundColor: "white" }}
      >
        <Container>
          <Col sm="4">
            <Row className="row-no-gutters py-1">
              <Col sm="3">
                <img src={pokemonLogo} width={50} height={50} />
              </Col>
              <Col sm="6" className="pt-2 justify-content-left">
                <h4 className="d-flex">Pokedex</h4>
              </Col>
            </Row>
          </Col>
        </Container>
      </nav>
      <Container
        className="pokemon-list"
        style={{
          width: "700px",
          marginTop: `${
            20 + document.getElementsByClassName("nav-height")[0]?.clientHeight
          }px`,
          border: "1px solid black",
          padding: "5px",
          background: "white",
        }}
      >
        {pokemonData?.results.map((data, key) => (
          <Row key={`${data?.name}${key}`} className="ml-2">
            <Col tag="h5" sm={6} className="text-start">
              {data?.name.charAt(0).toUpperCase() + data?.name.slice(1)}
            </Col>
          </Row>
        ))}
        <Spinner color="primary" size="sm"></Spinner>
      </Container>
    </div>
  );
}

export default App;
