import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import {
  Badge,
  Button,
  Card,
  CardBody,
  CardImg,
  CardSubtitle,
  CardText,
  CardTitle,
  Col,
} from "reactstrap";

const PokemonCard = ({ url, tag }) => {
  const [pokemonDetails, setPokemonDetails] = useState({});
  useEffect(() => {
    axios.get(url).then((res) => {
      setPokemonDetails(res.data);
      console.info("res.data", res.data);
    });
  }, []);
  console.info("pokemonDetails", pokemonDetails?.species?.name);
  return (
    <Col sm="3">
      <Card style={{ border: "none" }}>
        <CardImg
          alt="Card image cap"
          src={
            pokemonDetails?.sprites?.other?.["official-artwork"]?.front_default
          }
          style={{ backgroundColor: "#d7d5d5", borderRadius: "0.5rem" }}
          top
          width="100%"
        />
        <CardBody>
          <CardSubtitle className="mb-2 text-muted d-flex" tag="h6">
            {tag}
          </CardSubtitle>
          <CardText className="d-flex" tag="h5">
            {" "}
            {pokemonDetails?.species?.name &&
              pokemonDetails?.species?.name.charAt(0).toUpperCase() +
                pokemonDetails?.species?.name?.slice(1)}
          </CardText>
          {pokemonDetails?.types.map((type) => {
            return <Badge color="red">{type?.name}</Badge>;
          })}
          {/* <Link
            to={`pokemon-details/${pokemonDetails?.species?.name}`}
            state={pokemonDetails}
          >
            <Button>Button</Button>
          </Link> */}
        </CardBody>
      </Card>
    </Col>
  );
};

export default PokemonCard;
