import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import pokemonLogo from "./images/pokemon.png";
import {
  Button,
  Card,
  CardBody,
  CardGroup,
  CardImg,
  CardSubtitle,
  CardText,
  CardTitle,
  Col,
  Container,
  Row,
} from "reactstrap";
function App() {
  const [pokemonList, setPokemonList] = useState([]);
  const [pokemonCount, setPokemonCount] = useState();

  useEffect(() => {
    setCarData(page > 2 ? (prevState) => [...prevState, ...data] : data);
    setLazyLoading(false);
    canFetchMoreCars.current = true;
    setTotalCars(carList.total_count);
  }, [data, carList.total_count]);

  const handleScroll = () => {
    const element = document.getElementsByClassName("Bidwheelz-wrapper")
      ? document.getElementsByClassName("Bidwheelz-wrapper")[0]
      : "";
    const element1 = document.getElementsByClassName("page-classified")
      ? document.getElementsByClassName("page-classified")[0]
      : "";
    if (
      (element ? element.offsetHeight : 0) + element.scrollTop >
        (element1 ? element1.offsetHeight : 1000) - 1000 &&
      canFetchMoreCars.current
    ) {
      canFetchMoreCars.current = false;
      setLazyLoading(true);
    }
  };
  useEffect(() => {
    element.addEventListener("scroll", handleScroll);
    axios.get("https://pokeapi.co/api/v2/pokemon/").then((res) => {
      console.info(res, res);
      setPokemonList(res.data.results);
      setPokemonCount(res.data.count);
    });
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);
  return (
    <div className="App">
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <Row className="d-flex w-100">
          <Col sm="4">
            <Row className="row-no-gutters">
              <Col sm="3">
                <img src={pokemonLogo} width={50} height={50} />
              </Col>
              <Col sm="6" className="pt-2 justify-content-left">
                <h4 className="d-flex">Pokedex</h4>
              </Col>
            </Row>
          </Col>
        </Row>
      </nav>
      <Container className="pokemon-list">
        {pokemonList.map((data) => (
          <Row>
            <Col tag="h5" sm={6} className="text-start">
              {data?.name.charAt(0).toUpperCase() + data?.name.slice(1)}
            </Col>
          </Row>
        ))}
      </Container>
    </div>
  );
}

export default App;
