import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import pokemonLogo from "./images/pokemon.png";
import {
  Button,
  Card,
  CardBody,
  CardGroup,
  CardImg,
  CardSubtitle,
  CardText,
  CardTitle,
  Col,
  Container,
  Row,
} from "reactstrap";
function App() {
  const [pokemonData, setPokemonData] = useState({ results: [] });
  const [isLoading, setIsLoading] = useState(false);
  const [fetchMore, setFetchMOre] = useState(false);
  useEffect(() => {
    if (fetchMore) {
      setIsLoading(true);
      axios.get(pokemonData.next).then((res) => {
        // console.info(res, res);
        setPokemonData({
          ...pokemonData,
          ...res.data,
          results: [...pokemonData.results, ...res.data.results],
        });
      });
    }
    setIsLoading(false);
    setFetchMOre(false);
  }, [fetchMore]);
  const handleScroll = () => {
    const root = document.getElementById("root");
    console.info("root", root);
    // const element = document.getElementsByClassName("Bidwheelz-wrapper")
    //   ? document.getElementsByClassName("Bidwheelz-wrapper")[0]
    //   : "";
    // const element1 = document.getElementsByClassName("page-classified")
    //   ? document.getElementsByClassName("page-classified")[0]
    //   : "";

    if (
      document.documentElement.scrollHeight - 30 <
      document.documentElement.scrollTop + document.documentElement.clientHeight
    ) {
      setFetchMOre(true);
    }
    // if (
    //   (element ? element.offsetHeight : 0) + element.scrollTop >
    //     (element1 ? element1.offsetHeight : 1000) - 1000 &&
    //   canFetchMoreCars.current
    // ) {

    // }
  };
  useEffect(() => {
    document.addEventListener("scroll", handleScroll);
    axios.get("https://pokeapi.co/api/v2/pokemon/").then((res) => {
      setPokemonData(res.data);
    });
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div className="App">
      <nav className="navbar fixed-top nav-height">
        <Container>
          <Col sm="4">
            <Row className="row-no-gutters py-1">
              <Col sm="3">
                <img src={pokemonLogo} width={50} height={50} />
              </Col>
              <Col sm="6" className="pt-2 justify-content-left">
                <h4 className="d-flex">Pokedex</h4>
              </Col>
            </Row>
          </Col>
        </Container>
      </nav>
      <Container
        className="pokemon-list "
        style={{
          width: "700px",
          marginTop: `${
            20 + document.getElementsByClassName("nav-height")[0]?.clientHeight
          }px`,
        }}
      >
        {pokemonData?.results.map((data, key) => (
          <Row key={`${data?.name}${key}`}>
            <Col tag="h5" sm={6} className="text-start">
              {data?.name.charAt(0).toUpperCase() + data?.name.slice(1)}
            </Col>
          </Row>
        ))}
        {/* {pokemonData.map((data) => (
          <Row>
            <Col tag="h5" sm={6} className="text-start">
              {data?.name.charAt(0).toUpperCase() + data?.name.slice(1)}
            </Col>
          </Row>
        ))} */}
      </Container>
    </div>
  );
}

export default App;
